mapper.insert:
insert into employees(  )
values(#{departmentId}, #{jobId}, #{empForm}, #{position}
		, #{empNum}, #{empName}, null, null
		, #{empAddr}, #{empAddrDetail}, #{empPost}, #{empPhone}
		, #{empJumin}, #{empEmail}, #{empHireDate}, #{urgentPhone})

mapper.select:
select * from(select row_number() over(order by department_id) as rn, from employees
emp_num like '%' || #{searchWord} || '%' or department_id like '%' || #{searchWord} || '%' or job_id like '%' || #{searchWord} || '%'
) where rn between #{startRow} and #{endRow}
